let nameError=document.getElementById("nameError"),phoneError=document.getElementById("phoneError"),emailError=document.getElementById("emailError"),messageError=document.getElementById("messageError"),submitError=document.getElementById("submitError");function validateName(){let name=document.getElementById("contactName").value;return 0==name.length?(nameError.innerHTML="Name is required!",!1):name.match(/^[A-Za-z]*\s{1}[A-Za-z]*$/)?(nameError.innerHTML='<i class="fa-solid fa-circle-check"></i>',!0):(nameError.innerHTML="Write full name!",!1)}function validatePhone(){let phone=document.getElementById("contactPhone").value;return 0==phone.length?(phoneError.innerHTML="Phone is required!",!1):11!==phone.length?(phoneError.innerHTML="Phone should be 11 digits",!1):phone.match(/^[0-9]{11}$/)?(phoneError.innerHTML='<i class="fa-solid fa-circle-check"></i>',!0):(phoneError.innerHTML="Only digits please.",!1)}function validateEmail(){let email=document.getElementById("contactEmail").value;return 0==email.length?(emailError.innerHTML="Email is required!",!1):email.match(/^[A-Za-z\._\-[0-9]*[@][A-Za-z]*[\.][a-z]{2,4}$/)?(emailError.innerHTML='<i class="fa-solid fa-circle-check"></i>',!0):(emailError.innerHTML="Invalid Email",!1)}function validateText(){let message,required=30,left=30-document.getElementById("contactText").value.length;if(left>0)return messageError.innerHTML=left+" more  characters required",!1;messageError.innerHTML='<i class="fa-solid fa-circle-check"></i>'}function validateForm(){if(!(validateName()&&validatePhone()&&validateEmail()&&validateText()))return submitError.style.display="block",submitError.innerHTML="Please fix errors to submit!",setTimeout((function(){submitError.style.display="none"}),3e3),!1}